sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || !g_form.isNewRecord || newValue === '' || newValue == oldValue) {
      return;
   }
	//alert(g_form.getValue('u_requested_for'));
	var getEmailAdd = new GlideAjax('GetEmailAddress');
	getEmailAdd.addParam('sysparm_name', 'getEmail');
	getEmailAdd.addParam('sysparm_UserID', g_form.getValue('u_requested_for'));
	getEmailAdd.getXML(populateEmail);

	function populateEmail(response){
		var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_requested_email_for');
		//alert('emailFromScriptInclude');
		g_form.setValue('u_requested_email_for', emailFromScriptInclude);
	}

   //Type appropriate comment here, and begin script below

}
